'use client'

import { useState, useEffect } from 'react'
import { LeaderboardService } from '@/lib/leaderboard'
import { LeaderboardEntry } from '@/lib/types'
import SearchBar from '@/components/SearchBar'
import ProfilePicture from '@/components/ProfilePicture'
import { AlumniProfile } from '@/lib/supabase'
import { TrendingUp, TrendingDown, Trophy, Medal, Award, ExternalLink, Home } from 'lucide-react'
import { LiquidButton, LiquidGlassCard } from '@/components/ui/liquid-glass-button'
import { CoolLoader } from '@/components/ui/cool-loader'

export default function LeaderboardPage() {
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([])
  const [dailyChanges, setDailyChanges] = useState<Record<string, number>>({})
  const [loading, setLoading] = useState(true)
  const [searching, setSearching] = useState(false)
  const [searchQuery, setSearchQuery] = useState('')

  const loadLeaderboard = async () => {
    setLoading(true)
    try {
      const [leaderboardData, changesData] = await Promise.all([
        LeaderboardService.getLeaderboard(100),
        LeaderboardService.getDailyChanges()
      ])

      setLeaderboard(leaderboardData)
      setDailyChanges(changesData)
    } catch (error) {
      console.error('Error loading leaderboard:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleSearch = async (query: string) => {
    setSearchQuery(query)

    if (!query.trim()) {
      loadLeaderboard()
      return
    }

    setSearching(true)
    try {
      const searchResults = await LeaderboardService.searchLeaderboard(query)
      setLeaderboard(searchResults)
    } catch (error) {
      console.error('Error searching leaderboard:', error)
    } finally {
      setSearching(false)
    }
  }

  const getRankIcon = (rank: number) => {
    if (rank === 1) return <Trophy className="w-4 h-4 sm:w-5 sm:h-5 text-yellow-500" />
    if (rank === 2) return <Medal className="w-4 h-4 sm:w-5 sm:h-5 text-gray-400" />
    if (rank === 3) return <Award className="w-4 h-4 sm:w-5 sm:h-5 text-amber-600" />
    return <span className="text-sm sm:text-lg font-bold text-gray-600">#{rank}</span>
  }

  const getChangeIcon = (change: number) => {
    if (change > 0) return <TrendingUp className="w-4 h-4 text-green-500" />
    if (change < 0) return <TrendingDown className="w-4 h-4 text-red-500" />
    return null
  }

  useEffect(() => {
    loadLeaderboard()
  }, [])

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="text-center py-12">
          <div className="flex justify-center mb-4">
            <CoolLoader size="lg" />
          </div>
          <p className="text-gray-300">Loading leaderboard...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen py-8 px-4 sm:px-6 lg:px-8">
      {/* Navigation Buttons */}
      <div className="absolute top-6 left-6 flex space-x-3">
        <LiquidButton asChild size="lg">
          <a href="/" className="inline-flex items-center font-medium rounded-full">
            <Home className="w-5 h-5 mr-2" />
            Home
          </a>
        </LiquidButton>
        <LiquidButton asChild size="lg">
          <a href="/vote" className="inline-flex items-center font-medium rounded-full">
            <Trophy className="w-5 h-5 mr-2" />
            Vote
          </a>
        </LiquidButton>
        <LiquidButton asChild size="lg" variant="primary">
          <a href="/leaderboard" className="inline-flex items-center font-medium rounded-full">
            <TrendingUp className="w-5 h-5 mr-2" />
            Leaderboard
          </a>
        </LiquidButton>
      </div>

      <div className="max-w-4xl mx-auto pt-16">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-white mb-2">
            DVHS Alumni Leaderboard
          </h1>
          <p className="text-gray-300">
            Live ELO rankings of all DVHS alumni
          </p>
        </div>

      {/* Search Bar */}
      <div className="mb-8">
        <SearchBar
          onSearch={handleSearch}
          placeholder="Search for a specific alumni..."
        />
        {searching && (
          <div className="text-center mt-4">
            <CoolLoader size="sm" />
          </div>
        )}
      </div>

      {/* Leaderboard */}
      <LiquidGlassCard>
        <div className="bg-white/5 px-6 py-4 border-b border-gray-700">
          <h2 className="text-lg font-semibold text-white">
            {searchQuery ? `Search Results for "${searchQuery}"` : 'Top Rankings'}
          </h2>
          <p className="text-sm text-gray-400">
            {searchQuery ? `${leaderboard.length} results` : `${leaderboard.length} alumni ranked`}
          </p>
        </div>

        {leaderboard.length === 0 ? (
          <div className="text-center py-12">
            <Trophy className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-400 text-lg">No alumni found.</p>
            <p className="text-gray-500 text-sm mt-2">
              {searchQuery ? 'Try a different search term.' : 'Check back later for rankings.'}
            </p>
          </div>
        ) : (
          <div 
            className="divide-y divide-gray-200 max-h-[600px] overflow-y-auto scrollbar-custom"
            style={{
              scrollbarWidth: 'thin',
              scrollbarColor: '#3B82F6 rgba(255,255,255,0.1)',
              WebkitOverflowScrolling: 'touch',
              overscrollBehavior: 'contain'
            }}
          >
            {leaderboard.map((entry) => (
              <div 
                key={entry.id} 
                className={`px-3 sm:px-6 py-3 sm:py-4 transition-all duration-200 ${
                  entry.linkedin_url 
                    ? 'hover:bg-linkedin-blue hover:bg-opacity-5 cursor-pointer hover:shadow-sm border-l-4 border-transparent hover:border-linkedin-blue' 
                    : 'hover:bg-gray-50'
                }`}
                onClick={() => {
                  if (entry.linkedin_url) {
                    window.open(entry.linkedin_url, '_blank')
                  }
                }}
                title={entry.linkedin_url ? "Click to view LinkedIn profile" : ""}
              >
                <div className="flex items-center justify-between gap-2">
                  <div className="flex items-center gap-2 sm:gap-4 flex-1 min-w-0">
                    {/* Rank */}
                    <div className="flex items-center justify-center w-8 sm:w-12 flex-shrink-0">
                      {getRankIcon(entry.rank)}
                    </div>

                    {/* Profile Picture */}
                    <div className="flex-shrink-0">
                      <ProfilePicture
                        profile={{
                          id: entry.id,
                          name: entry.name,
                          profile_picture_url: entry.profile_picture_url,
                          location: '',
                          experiences: [],
                          education: [],
                          created_at: '',
                          updated_at: '',
                          elo: entry.elo
                        }}
                        size="md"
                      />
                    </div>

                    {/* Name and ELO */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-1 sm:gap-2">
                        <h3 className="text-sm sm:text-lg font-semibold text-white break-words">
                          {entry.name}
                        </h3>
                        {entry.linkedin_url && (
                          <ExternalLink className="w-3 h-3 sm:w-4 sm:h-4 text-white flex-shrink-0" />
                        )}
                      </div>
                      <div className="flex items-center gap-1 sm:gap-2 flex-wrap">
                        <span className="text-xs sm:text-sm text-white font-semibold">
                          ELO: {entry.elo}
                        </span>
                        {dailyChanges[entry.id] !== undefined && dailyChanges[entry.id] !== 0 && (
                          <div className="flex items-center gap-0.5 sm:gap-1">
                            {getChangeIcon(dailyChanges[entry.id])}
                            <span className={`text-xs sm:text-sm font-medium ${
                              dailyChanges[entry.id] > 0 ? 'text-green-600' : 'text-red-600'
                            }`}>
                              {dailyChanges[entry.id] > 0 ? '+' : ''}{dailyChanges[entry.id]}
                            </span>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>

                  {/* ELO Score - Hidden on mobile */}
                  <div className="text-right hidden sm:block">
                    <div className="text-2xl font-bold text-white">
                      {entry.elo}
                    </div>
                    <div className="text-sm text-gray-400">
                      ELO Rating
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </LiquidGlassCard>

        {/* Footer Info */}
        <div className="mt-8 text-center text-sm text-gray-400">
          <p>
            Rankings are updated in real-time based on community votes.
            <br />
            ELO ratings start at 2000 for new profiles.
          </p>
        </div>
      </div>
    </div>
  )
}

hooks/useRealtimeUpdates.ts.content:
'use client'

import { useEffect, useState } from 'react'
import { supabase, AlumniProfile } from '@/lib/supabase'
import { RealtimeChannel } from '@supabase/supabase-js'

export interface RealtimeUpdate {
  type: 'elo_change' | 'new_vote' | 'profile_update'
  data: any
  timestamp: string
}

export function useRealtimeUpdates() {
  const [updates, setUpdates] = useState<RealtimeUpdate[]>([])
  const [channel, setChannel] = useState<RealtimeChannel | null>(null)

  useEffect(() => {
    // Create subscription channel
    const realtimeChannel = supabase
      .channel('public_updates')
      .on(
        'postgres_changes',
        {
          event: 'UPDATE',
          schema: 'public',
          table: 'alumni_profiles',
          filter: 'elo'
        },
        (payload) => {
          setUpdates(prev => [...prev.slice(-9), {
            type: 'elo_change',
            data: payload,
            timestamp: new Date().toISOString()
          }])
        }
      )
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'vote_sessions'
        },
        (payload) => {
          setUpdates(prev => [...prev.slice(-9), {
            type: 'new_vote',
            data: payload,
            timestamp: new Date().toISOString()
          }])
        }
      )
      .subscribe()

    setChannel(realtimeChannel)

    return () => {
      realtimeChannel.unsubscribe()
    }
  }, [])

  return { updates, channel }
}

export function useRealtimeLeaderboard() {
  const [leaderboard, setLeaderboard] = useState<AlumniProfile[]>([])
  const [loading, setLoading] = useState(true)

  const loadLeaderboard = async () => {
    try {
      const { data, error } = await supabase
        .from('alumni_profiles')
        .select(`
          *,
          experiences (*),
          education (*)
        `)
        .order('elo', { ascending: false })
        .limit(100)

      if (error) throw error
      setLeaderboard(data || [])
    } catch (error) {
      console.error('Error loading leaderboard:', error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    loadLeaderboard()

    // Subscribe to ELO changes
    const channel = supabase
      .channel('leaderboard_updates')
      .on(
        'postgres_changes',
        {
          event: 'UPDATE',
          schema: 'public',
          table: 'alumni_profiles',
          filter: 'elo'
        },
        () => {
          // Reload leaderboard when ELO changes
          loadLeaderboard()
        }
      )
      .subscribe()

    return () => {
      channel.unsubscribe()
    }
  }, [])

  return { leaderboard, loading, refreshLeaderboard: loadLeaderboard }
}

export function useRealtimeStats() {
  const [stats, setStats] = useState<{
    totalProfiles: number
    totalVotes: number
    averageElo: number
  } | null>(null)

  const loadStats = async () => {
    try {
      const [
        { count: totalProfiles },
        { count: totalVotes },
        { data: profiles }
      ] = await Promise.all([
        supabase.from('alumni_profiles').select('*', { count: 'exact', head: true }),
        supabase.from('vote_sessions').select('*', { count: 'exact', head: true }),
        supabase.from('alumni_profiles').select('elo')
      ])

      const averageElo = profiles ? 
        Math.round(profiles.reduce((sum, p) => sum + p.elo, 0) / profiles.length) : 2000

      setStats({
        totalProfiles: totalProfiles || 0,
        totalVotes: totalVotes || 0,
        averageElo
      })
    } catch (error) {
      console.error('Error loading stats:', error)
    }
  }

  useEffect(() => {
    loadStats()

    // Subscribe to changes
    const channel = supabase
      .channel('stats_updates')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'alumni_profiles'
        },
        () => {
          loadStats()
        }
      )
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'vote_sessions'
        },
        () => {
          loadStats()
        }
      )
      .subscribe()

    return () => {
      channel.unsubscribe()
    }
  }, [])

  return { stats, refreshStats: loadStats }
}

components/ui/waves-background.tsx.content:
"use client"

import { useRef, useEffect } from "react"
import { cn } from "@/lib/utils"

interface WavesProps {
  lineColor?: string
  backgroundColor?: string
  waveSpeedX?: number
  waveSpeedY?: number
  waveAmpX?: number
  waveAmpY?: number
  xGap?: number
  yGap?: number
  friction?: number
  tension?: number
  maxCursorMove?: number
  className?: string
}

class Grad {
  x: number
  y: number
  z: number

  constructor(x: number, y: number, z: number) {
    this.x = x
    this.y = y
    this.z = z
  }
  dot2(x: number, y: number) {
    return this.x * x + this.y * y
  }
}

class Noise {
  grad3: Grad[]
  p: number[]
  perm: number[]
  gradP: Grad[]

  constructor(seed = 0) {
    this.grad3 = [
      new Grad(1, 1, 0),
      new Grad(-1, 1, 0),
      new Grad(1, -1, 0),
      new Grad(-1, -1, 0),
      new Grad(1, 0, 1),
      new Grad(-1, 0, 1),
      new Grad(1, 0, -1),
      new Grad(-1, 0, -1),
      new Grad(0, 1, 1),
      new Grad(0, -1, 1),
      new Grad(0, 1, -1),
      new Grad(0, -1, -1),
    ]
    this.p = [
      151, 160, 137, 91, 90, 15, 131, 13, 201, 95, 96, 53, 194, 233, 7, 225,
      140, 36, 103, 30, 69, 142, 8, 99, 37, 240, 21, 10, 23, 190, 6, 148, 247,
      120, 234, 75, 0, 26, 197, 62, 94, 252, 219, 203, 117, 35, 11, 32, 57, 177,
      33, 88, 237, 149, 56, 87, 174, 20, 125, 136, 171, 168, 68, 175, 74, 165,
      71, 134, 139, 48, 27, 166, 77, 146, 158, 231, 83, 111, 229, 122, 60, 211,
      133, 230, 220, 105, 92, 41, 55, 46, 245, 40, 244, 102, 143, 54, 65, 25,
      63, 161, 1, 216, 80, 73, 209, 76, 132, 187, 208, 89, 18, 169, 200, 196,
      135, 130, 116, 188, 159, 86, 164, 100, 109, 198, 173, 186, 3, 64, 52, 217,
      226, 250, 124, 123, 5, 202, 38, 147, 118, 126, 255, 82, 85, 212, 207, 206,
      59, 227, 47, 16, 58, 17, 182, 189, 28, 42, 223, 183, 170, 213, 119, 248,
      152, 2, 44, 154, 163, 70, 221, 153, 101, 155, 167, 43, 172, 9, 129, 22,
      39, 253, 19, 98, 108, 110, 79, 113, 224, 232, 178, 185, 112, 104, 218,
      246, 97, 228, 251, 34, 242, 193, 238, 210, 144, 12, 191, 179, 162, 241,
      81, 51, 145, 235, 249, 14, 239, 107, 49, 192, 214, 31, 181, 199, 106, 157,
      184, 84, 204, 176, 115, 121, 50, 45, 127, 4, 150, 254, 138, 236, 205, 93,
      222, 114, 67, 29, 24, 72, 243, 141, 128, 195, 78, 66, 215, 61, 156, 180,
    ]
    this.perm = new Array(512)
    this.gradP = new Array(512)
    this.seed(seed)
  }
  seed(seed: number) {
    if (seed > 0 && seed < 1) seed *= 65536
    seed = Math.floor(seed)
    if (seed < 256) seed |= seed << 8
    for (let i = 0; i < 256; i++) {
      let v = i & 1 ? this.p[i] ^ (seed & 255) : this.p[i] ^ ((seed >> 8) & 255)
      this.perm[i] = this.perm[i + 256] = v
      this.gradP[i] = this.gradP[i + 256] = this.grad3[v % 12]
    }
  }
  fade(t: number) {
    return t * t * t * (t * (t * 6 - 15) + 10)
  }
  lerp(a: number, b: number, t: number) {
    return (1 - t) * a + t * b
  }
  perlin2(x: number, y: number) {
    let X = Math.floor(x),
      Y = Math.floor(y)
    x -= X
    y -= Y
    X &= 255
    Y &= 255
    const n00 = this.gradP[X + this.perm[Y]].dot2(x, y)
    const n01 = this.gradP[X + this.perm[Y + 1]].dot2(x, y - 1)
    const n10 = this.gradP[X + 1 + this.perm[Y]].dot2(x - 1, y)
    const n11 = this.gradP[X + 1 + this.perm[Y + 1]].dot2(x - 1, y - 1)
    const u = this.fade(x)
    return this.lerp(
      this.lerp(n00, n10, u),
      this.lerp(n01, n11, u),
      this.fade(y),
    )
  }
}

export function Waves({
  lineColor = "rgba(0, 119, 181, 0.3)",
  backgroundColor = "transparent",
  waveSpeedX = 0.0125,
  waveSpeedY = 0.005,
  waveAmpX = 32,
  waveAmpY = 16,
  xGap = 10,
  yGap = 32,
  friction = 0.925,
  tension = 0.005,
  maxCursorMove = 100,
  className,
}: WavesProps) {
  const containerRef = useRef<HTMLDivElement>(null)
  const canvasRef = useRef<HTMLCanvasElement>(null)
  const ctxRef = useRef<CanvasRenderingContext2D | null>(null)
  const boundingRef = useRef({ width: 0, height: 0, left: 0, top: 0 })
  const noiseRef = useRef(new Noise(Math.random()))
  const linesRef = useRef<any[]>([])
  const mouseRef = useRef({
    x: -10,
    y: 0,
    lx: 0,
    ly: 0,
    sx: 0,
    sy: 0,
    v: 0,
    vs: 0,
    a: 0,
    set: false,
  })

  useEffect(() => {
    const canvas = canvasRef.current
    const container = containerRef.current
    if (!canvas || !container) return

    ctxRef.current = canvas.getContext("2d")

    function setSize() {
      if (!container) return
      boundingRef.current = container.getBoundingClientRect()
      if (canvas) {
        canvas.width = boundingRef.current.width
        canvas.height = boundingRef.current.height
      }
    }

    function setLines() {
      const { width, height } = boundingRef.current
      linesRef.current = []
      const oWidth = width + 200,
        oHeight = height + 30
      const totalLines = Math.ceil(oWidth / xGap)
      const totalPoints = Math.ceil(oHeight / yGap)
      const xStart = (width - xGap * totalLines) / 2
      const yStart = (height - yGap * totalPoints) / 2
      for (let i = 0; i <= totalLines; i++) {
        const pts = []
        for (let j = 0; j <= totalPoints; j++) {
          pts.push({
            x: xStart + xGap * i,
            y: yStart + yGap * j,
            wave: { x: 0, y: 0 },
            cursor: { x: 0, y: 0, vx: 0, vy: 0 },
          })
        }
        linesRef.current.push(pts)
      }
    }

    function movePoints(time: number) {
      const lines = linesRef.current
      const mouse = mouseRef.current
      const noise = noiseRef.current
      lines.forEach((pts) => {
        pts.forEach((p: any) => {
          const move =
            noise.perlin2(
              (p.x + time * waveSpeedX) * 0.002,
              (p.y + time * waveSpeedY) * 0.0015,
            ) * 12
          p.wave.x = Math.cos(move) * waveAmpX
          p.wave.y = Math.sin(move) * waveAmpY

          const dx = p.x - mouse.sx,
            dy = p.y - mouse.sy
          const dist = Math.hypot(dx, dy),
            l = Math.max(175, mouse.vs)
          if (dist < l) {
            const s = 1 - dist / l
            const f = Math.cos(dist * 0.001) * s
            p.cursor.vx += Math.cos(mouse.a) * f * l * mouse.vs * 0.00065
            p.cursor.vy += Math.sin(mouse.a) * f * l * mouse.vs * 0.00065
          }

          p.cursor.vx += (0 - p.cursor.x) * tension
          p.cursor.vy += (0 - p.cursor.y) * tension
          p.cursor.vx *= friction
          p.cursor.vy *= friction
          p.cursor.x += p.cursor.vx * 2
          p.cursor.y += p.cursor.vy * 2

          p.cursor.x = Math.min(
            maxCursorMove,
            Math.max(-maxCursorMove, p.cursor.x),
          )
          p.cursor.y = Math.min(
            maxCursorMove,
            Math.max(-maxCursorMove, p.cursor.y),
          )
        })
      })
    }

    function moved(point: any, withCursor = true) {
      const x = point.x + point.wave.x + (withCursor ? point.cursor.x : 0)
      const y = point.y + point.wave.y + (withCursor ? point.cursor.y : 0)
      return { x: Math.round(x * 10) / 10, y: Math.round(y * 10) / 10 }
    }

    function drawLines() {
      const { width, height } = boundingRef.current
      const ctx = ctxRef.current
      if (!ctx) return
      ctx.clearRect(0, 0, width, height)

      // Create bright blue gradient
      const gradient = ctx.createLinearGradient(0, 0, width, 0)
      gradient.addColorStop(0, 'rgba(59, 130, 246, 0.8)')    // bright blue
      gradient.addColorStop(0.5, 'rgba(96, 165, 250, 0.7)')  // lighter blue
      gradient.addColorStop(1, 'rgba(147, 197, 253, 0.6)')   // sky blue

      ctx.beginPath()
      ctx.strokeStyle = gradient
      linesRef.current.forEach((points) => {
        let p1 = moved(points[0], false)
        ctx.moveTo(p1.x, p1.y)
        points.forEach((p: any, idx: number) => {
          const isLast = idx === points.length - 1
          p1 = moved(p, !isLast)
          const p2 = moved(
            points[idx + 1] || points[points.length - 1],
            !isLast,
          )
          ctx.lineTo(p1.x, p1.y)
          if (isLast) ctx.moveTo(p2.x, p2.y)
        })
      })
      ctx.stroke()
    }

    function tick(t: number) {
      const mouse = mouseRef.current

      mouse.sx += (mouse.x - mouse.sx) * 0.1
      mouse.sy += (mouse.y - mouse.sy) * 0.1

      const dx = mouse.x - mouse.lx,
        dy = mouse.y - mouse.ly
      const d = Math.hypot(dx, dy)
      mouse.v = d
      mouse.vs += (d - mouse.vs) * 0.1
      mouse.vs = Math.min(100, mouse.vs)
      mouse.lx = mouse.x
      mouse.ly = mouse.y
      mouse.a = Math.atan2(dy, dx)

      if (container) {
        container.style.setProperty("--x", `${mouse.sx}px`)
        container.style.setProperty("--y", `${mouse.sy}px`)
      }

      movePoints(t)
      drawLines()
      requestAnimationFrame(tick)
    }

    function onResize() {
      setSize()
      setLines()
    }
    function onMouseMove(e: MouseEvent) {
      updateMouse(e.pageX, e.pageY)
    }
    function onTouchMove(e: TouchEvent) {
      e.preventDefault()
      const touch = e.touches[0]
      updateMouse(touch.clientX, touch.clientY)
    }
    function updateMouse(x: number, y: number) {
      const mouse = mouseRef.current
      const b = boundingRef.current
      mouse.x = x - b.left
      mouse.y = y - b.top + window.scrollY
      if (!mouse.set) {
        mouse.sx = mouse.x
        mouse.sy = mouse.y
        mouse.lx = mouse.x
        mouse.ly = mouse.y
        mouse.set = true
      }
    }

    setSize()
    setLines()
    requestAnimationFrame(tick)
    window.addEventListener("resize", onResize)
    window.addEventListener("mousemove", onMouseMove)
    window.addEventListener("touchmove", onTouchMove, { passive: false })

    return () => {
      window.removeEventListener("resize", onResize)
      window.removeEventListener("mousemove", onMouseMove)
      window.removeEventListener("touchmove", onTouchMove)
    }
  }, [
    lineColor,
    backgroundColor,
    waveSpeedX,
    waveSpeedY,
    waveAmpX,
    waveAmpY,
    friction,
    tension,
    maxCursorMove,
    xGap,
    yGap,
  ])

  return (
    <div
      ref={containerRef}
      style={{
        backgroundColor,
      }}
      className={cn(
        "absolute top-0 left-0 w-full h-full overflow-hidden",
        className,
      )}
    >
      <div
        className={cn(
          "absolute top-0 left-0 rounded-full",
          "w-2 h-2 bg-foreground/10",
        )}
        style={{
          transform:
            "translate3d(calc(var(--x) - 50%), calc(var(--y) - 50%), 0)",
          willChange: "transform",
        }}
      />
      <canvas ref={canvasRef} className="block w-full h-full" />
    </div>
  )
}

