import { AlumniProfile, supabase } from './supabase'
import { LeaderboardEntry } from './types'

export class LeaderboardService {
  static async getLeaderboard(limit: number = 100): Promise<LeaderboardEntry[]> {
    try {
      const { data, error } = await supabase
        .from('alumni_profiles')
        .select(`
          id,
          name,
          elo,
          profile_picture_url,
          linkedin_url,
          experiences (*),
          education (*)
        `)
        .order('elo', { ascending: false })
        .limit(limit)

      if (error) {
        console.error('Error fetching leaderboard:', error)
        return []
      }

      const leaderboard = (data || []).map((profile, index) => ({
        rank: index + 1,
        id: profile.id,
        name: profile.name,
        elo: profile.elo,
        change: 0, // Will be calculated separately for daily changes
        profile_picture_url: profile.profile_picture_url,
        linkedin_url: profile.linkedin_url
      }))

      return leaderboard
    } catch (error) {
      console.error('Error fetching leaderboard:', error)
      return []
    }
  }

  static async searchLeaderboard(query: string): Promise<LeaderboardEntry[]> {
    try {
      const { data, error } = await supabase
        .from('alumni_profiles')
        .select(`
          id,
          name,
          elo,
          profile_picture_url,
          linkedin_url,
          experiences (*),
          education (*)
        `)
        .ilike('name', `%${query}%`)
        .order('elo', { ascending: false })
        .limit(50)

      if (error) {
        console.error('Error searching leaderboard:', error)
        return []
      }

      const leaderboard = (data || []).map((profile, index) => ({
        rank: index + 1,
        id: profile.id,
        name: profile.name,
        elo: profile.elo,
        change: 0,
        profile_picture_url: profile.profile_picture_url,
        linkedin_url: profile.linkedin_url
      }))

      return leaderboard
    } catch (error) {
      console.error('Error searching leaderboard:', error)
      return []
    }
  }

  static async getProfileRank(profileId: string): Promise<number | null> {
    try {
      // Get the profile's ELO
      const { data: profile, error: profileError } = await supabase
        .from('alumni_profiles')
        .select('elo')
        .eq('id', profileId)
        .single()

      if (profileError || !profile) return null

      // Count how many profiles have higher ELO
      const { count, error: countError } = await supabase
        .from('alumni_profiles')
        .select('*', { count: 'exact', head: true })
        .gt('elo', profile.elo)

      if (countError) return null

      return (count || 0) + 1
    } catch (error) {
      console.error('Error getting profile rank:', error)
      return null
    }
  }

  static async getDailyChanges(): Promise<Record<string, number>> {
    try {
      const yesterday = new Date()
      yesterday.setDate(yesterday.getDate() - 1)
      yesterday.setHours(0, 0, 0, 0)

      const { data, error } = await supabase
        .from('elo_history')
        .select('alumni_id, change_amount')
        .gte('created_at', yesterday.toISOString())

      if (error) throw error

      const changes: Record<string, number> = {}
      ;(data || []).forEach(record => {
        changes[record.alumni_id] = (changes[record.alumni_id] || 0) + record.change_amount
      })

      return changes
    } catch (error) {
      console.error('Error getting daily changes:', error)
      return {}
    }
  }
}

components/ProfilePicture.tsx.content:
'use client'

import { AlumniProfile } from '@/lib/supabase'

interface ProfilePictureProps {
  profile: AlumniProfile
  size?: 'sm' | 'md' | 'lg' | 'xl'
  className?: string
}

const sizeClasses = {
  sm: 'w-8 h-8 text-sm',
  md: 'w-12 h-12 text-base',
  lg: 'w-16 h-16 text-lg',
  xl: 'w-24 h-24 text-xl'
}

export default function ProfilePicture({ profile, size = 'md', className = '' }: ProfilePictureProps) {
  const initials = profile.name
    .split(' ')
    .map(n => n[0])
    .join('')
    .toUpperCase()
    .slice(0, 2)

  // Generate background color based on name hash
  const colors = ['#0077B5', '#004182', '#0066CC', '#0052CC', '#003366']
  const colorIndex = profile.name.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0) % colors.length
  const backgroundColor = colors[colorIndex]

  // Use actual profile picture if available, otherwise show initials
  if (profile.profile_picture_url) {
    return (
      <img
        src={profile.profile_picture_url}
        alt={profile.name}
        className={`${sizeClasses[size]} rounded-full object-cover ${className}`}
        onError={(e) => {
          // Fallback to initials if image fails to load
          const target = e.target as HTMLImageElement
          target.style.display = 'none'
          const parent = target.parentElement
          if (parent) {
            parent.innerHTML = `<div class="${sizeClasses[size]} rounded-full flex items-center justify-center text-white font-semibold" style="background-color: ${backgroundColor}">${initials}</div>`
          }
        }}
      />
    )
  }

  return (
    <div 
      className={`${sizeClasses[size]} rounded-full flex items-center justify-center text-white font-semibold ${className}`}
      style={{ backgroundColor }}
    >
      {initials}
    </div>
  )
}

components/AlumniProfileCard.tsx.content:
'use client'

import { AlumniProfile } from '@/lib/supabase'
import ProfilePicture from './ProfilePicture'
import { ExternalLink, MapPin, Building, GraduationCap } from 'lucide-react'
import { LiquidGlassCard } from '@/components/ui/liquid-glass-button'
import { Inter } from 'next/font/google'

const inter = Inter({ subsets: ['latin'] })

interface AlumniProfileCardProps {
  profile: AlumniProfile
  showElo?: boolean
  showLinkedIn?: boolean
  blurred?: boolean
  eloChange?: number
  className?: string
  hoverLinkedIn?: boolean
}

export default function AlumniProfileCard({ 
  profile, 
  showElo = false, 
  showLinkedIn = true, 
  blurred = false,
  eloChange,
  className = '',
  hoverLinkedIn = false
}: AlumniProfileCardProps) {
  const primaryExperience = profile.experience_1_role && profile.experience_1_company 
    ? { role: profile.experience_1_role, company: profile.experience_1_company }
    : profile.experiences?.[0]
  const primaryEducation = profile.college_1_name && profile.college_1_degree
    ? { school: profile.college_1_name, degree: profile.college_1_degree }
    : profile.education?.[0]

  const cardContent = (
    <LiquidGlassCard transparent className={`profile-card ${hoverLinkedIn && profile.linkedin_url ? 'cursor-pointer hover:shadow-lg transition-shadow duration-200' : ''} ${className} ${inter.className}`}>
      {hoverLinkedIn && profile.linkedin_url && (
        <a
          href={profile.linkedin_url}
          target="_blank"
          rel="noopener noreferrer"
          className="absolute inset-0 z-10"
          title="View LinkedIn Profile"
        />
      )}
      <div className="flex flex-col sm:flex-row items-start sm:space-x-4 space-y-3 sm:space-y-0">
        <div className={`mx-auto sm:mx-0 ${blurred ? 'blur-content' : ''}`}>
          <ProfilePicture profile={profile} size="xl" />
        </div>
        <div className="flex-1 min-w-0 w-full">
          <div className="flex items-start justify-between gap-2">
            <h3 className={`text-sm sm:text-xl font-semibold break-words ${blurred ? 'blur-content text-gray-200' : 'text-white'}`}>
              {profile.name}
            </h3>
            {showElo && (
              <div className={`flex items-center space-x-1 flex-shrink-0 ${blurred ? 'blur-content' : ''}`}>
                <span className="text-xs sm:text-lg font-bold text-white">
                  {profile.elo}
                </span>
                {eloChange !== undefined && eloChange !== 0 && (
                  <span className={`text-xs ${eloChange > 0 ? 'elo-change-positive' : 'elo-change-negative'}`}>
                    {eloChange > 0 ? '+' : ''}{eloChange}
                  </span>
                )}
              </div>
            )}
          </div>

          {primaryExperience && (
            <div className={`mt-1 sm:mt-2 flex items-start ${blurred ? 'text-gray-200' : 'text-gray-300'}`}>
              <Building className="w-3 h-3 sm:w-4 sm:h-4 mr-1 mt-0.5 flex-shrink-0" />
              <span className={`text-[10px] leading-tight sm:text-sm break-words ${blurred ? 'blur-content' : ''}`}>
                {primaryExperience.role} at {primaryExperience.company}
              </span>
            </div>
          )}

          {profile.location && (
            <div className={`mt-1 flex items-start ${blurred ? 'text-gray-200' : 'text-gray-400'}`}>
              <MapPin className="w-3 h-3 sm:w-4 sm:h-4 mr-1 mt-0.5 flex-shrink-0" />
              <span className={`text-[10px] leading-tight sm:text-sm break-words ${blurred ? 'blur-content' : ''}`}>{profile.location}</span>
            </div>
          )}

          {primaryEducation && (
            <div className={`mt-1 sm:mt-2 flex items-start ${blurred ? 'text-gray-200' : 'text-gray-300'}`}>
              <GraduationCap className="w-3 h-3 sm:w-4 sm:h-4 mr-1 mt-0.5 flex-shrink-0" />
              <span className={`text-[10px] leading-tight sm:text-sm break-words ${blurred ? 'blur-content' : ''}`}>
                {primaryEducation.degree} at {primaryEducation.school}
              </span>
            </div>
          )}

          {showLinkedIn && profile.linkedin_url && !blurred && (
            <div className="mt-3">
              <a
                href={profile.linkedin_url}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center text-white hover:text-gray-300 text-xs sm:text-sm font-medium"
              >
                <ExternalLink className="w-3 h-3 sm:w-4 sm:h-4 mr-1" />
                View LinkedIn
              </a>
            </div>
          )}
        </div>
      </div>

      {/* Experience Section */}
      {(profile.experiences && profile.experiences.length > 0) || profile.experience_1_role ? (
        <div className="mt-3 sm:mt-6">
          <h4 className="text-[10px] sm:text-sm font-semibold text-white mb-1.5 sm:mb-3">Experience</h4>
          <div className="space-y-2 sm:space-y-3">
            {/* Show structured experience data first */}
            {[
              { role: profile.experience_1_role, company: profile.experience_1_company, logo: profile.experience_1_logo },
              { role: profile.experience_2_role, company: profile.experience_2_company, logo: profile.experience_2_logo },
              { role: profile.experience_3_role, company: profile.experience_3_company, logo: profile.experience_3_logo },
              { role: profile.experience_4_role, company: profile.experience_4_company, logo: profile.experience_4_logo }
            ].filter(exp => exp.role && exp.company).map((exp, index) => (
              <div key={index} className="flex items-start space-x-2 sm:space-x-3">
                {exp.logo ? (
                  <img src={exp.logo} alt={exp.company} className="w-6 h-6 sm:w-8 sm:h-8 rounded object-cover flex-shrink-0" />
                ) : (
                  <div className="w-6 h-6 sm:w-8 sm:h-8 bg-gray-200 rounded flex items-center justify-center flex-shrink-0">
                    <Building className="w-3 h-3 sm:w-4 sm:h-4 text-gray-600" />
                  </div>
                )}
                <div className="flex-1 min-w-0">
                  <p className="text-[10px] leading-tight sm:text-sm font-medium text-white break-words">{exp.role}</p>
                  <p className="text-[10px] leading-tight sm:text-sm text-gray-300 break-words">{exp.company}</p>
                </div>
              </div>
            ))}
            {/* Fallback to experiences array if no structured data */}
            {!profile.experience_1_role && profile.experiences && profile.experiences.map((exp, index) => (
              <div key={index} className="flex items-start space-x-2 sm:space-x-3">
                <div className="w-6 h-6 sm:w-8 sm:h-8 bg-gray-200 rounded flex items-center justify-center flex-shrink-0">
                  <Building className="w-3 h-3 sm:w-4 sm:h-4 text-gray-600" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-[10px] leading-tight sm:text-sm font-medium text-white break-words">{exp.role}</p>
                  <p className="text-[10px] leading-tight sm:text-sm text-gray-300 break-words">{exp.company}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : null}

      {/* Education Section */}
      {(profile.education && profile.education.length > 0) || profile.college_1_name ? (
        <div className="mt-3 sm:mt-6">
          <h4 className="text-[10px] sm:text-sm font-semibold text-white mb-1.5 sm:mb-3">Education</h4>
          <div className="space-y-2 sm:space-y-3">
            {/* Show structured education data first */}
            {[
              { school: profile.college_1_name, degree: profile.college_1_degree, logo: profile.college_1_logo },
              { school: profile.college_2_name, degree: profile.college_2_degree, logo: profile.college_2_logo },
              { school: profile.college_3_name, degree: profile.college_3_degree, logo: profile.college_3_logo }
            ].filter(edu => edu.school && edu.degree).map((edu, index) => (
              <div key={index} className="flex items-start space-x-2 sm:space-x-3">
                {edu.logo ? (
                  <img src={edu.logo} alt={edu.school} className="w-6 h-6 sm:w-8 sm:h-8 rounded object-cover flex-shrink-0" />
                ) : (
                  <div className="w-6 h-6 sm:w-8 sm:h-8 bg-gray-200 rounded flex items-center justify-center flex-shrink-0">
                    <GraduationCap className="w-3 h-3 sm:w-4 sm:h-4 text-gray-600" />
                  </div>
                )}
                <div className="flex-1 min-w-0">
                  <p className="text-[10px] leading-tight sm:text-sm font-medium text-white break-words">{edu.school}</p>
                  <p className="text-[10px] leading-tight sm:text-sm text-gray-300 break-words">{edu.degree}</p>
                </div>
              </div>
            ))}
            {/* Fallback to education array if no structured data */}
            {!profile.college_1_name && profile.education && profile.education.map((edu, index) => (
              <div key={index} className="flex items-start space-x-2 sm:space-x-3">
                <div className="w-6 h-6 sm:w-8 sm:h-8 bg-gray-200 rounded flex items-center justify-center flex-shrink-0">
                  <GraduationCap className="w-3 h-3 sm:w-4 sm:h-4 text-gray-600" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-[10px] leading-tight sm:text-sm font-medium text-white break-words">{edu.school}</p>
                  <p className="text-[10px] leading-tight sm:text-sm text-gray-300 break-words">{edu.degree}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : null}
    </LiquidGlassCard>
  )

  return cardContent
}

app/vote/page.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { AlumniProfile } from '@/lib/supabase'
import AlumniProfileCard from '@/components/AlumniProfileCard'
import { VotingService, VotingPair } from '@/lib/voting'
import { ArrowRight, RotateCcw, Trophy, Home, TrendingUp } from 'lucide-react'
import { LiquidButton } from '@/components/ui/liquid-glass-button'
import { CoolLoader } from '@/components/ui/cool-loader'

export default function VotePage() {
  const [votingPair, setVotingPair] = useState<VotingPair | null>(null)
  const [loading, setLoading] = useState(true)
  const [voted, setVoted] = useState(false)
  const [selectedWinner, setSelectedWinner] = useState<string | null>(null)
  const [votingEqual, setVotingEqual] = useState(false)
  const [eloChanges, setEloChanges] = useState<{
    profile1: { oldElo: number; newElo: number; change: number }
    profile2: { oldElo: number; newElo: number; change: number }
  } | null>(null)

  const loadNewPair = async () => {
    setLoading(true)
    setVoted(false)
    setSelectedWinner(null)
    setVotingEqual(false)
    setEloChanges(null)

    try {
      const pair = await VotingService.getRandomPair()
      setVotingPair(pair)
    } catch (error) {
      console.error('Error loading voting pair:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleVote = async (winnerId?: string) => {
    if (!votingPair) return

    setSelectedWinner(winnerId || null)
    setVotingEqual(!winnerId)
    setVoted(true)

    try {
      const result = await VotingService.submitVote(
        votingPair.profile1.id,
        votingPair.profile2.id,
        votingPair.sessionId,
        winnerId
      )

      setEloChanges(result.eloChanges)
    } catch (error) {
      console.error('Error submitting vote:', error)
    }
  }

  useEffect(() => {
    loadNewPair()
  }, [])

  if (loading) {
    return (
      <div className="max-w-6xl mx-auto">
        <div className="text-center py-12">
          <div className="flex justify-center mb-4">
            <CoolLoader size="lg" />
          </div>
          <p className="text-gray-300">Loading voting pair...</p>
        </div>
      </div>
    )
  }

  if (!votingPair) {
    return (
      <div className="max-w-6xl mx-auto">
        <div className="text-center py-12">
          <Trophy className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h2 className="text-2xl font-semibold text-gray-900 mb-2">No Profiles Available</h2>
          <p className="text-gray-600 mb-6">There aren't enough alumni profiles to create a voting pair.</p>
          <button
            onClick={loadNewPair}
            className="linkedin-button"
          >
            Try Again
          </button>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen py-8 px-4 sm:px-6 lg:px-8">
      {/* Navigation Buttons */}
      <div className="absolute top-6 left-6 flex space-x-3">
        <LiquidButton asChild size="lg">
          <a href="/" className="inline-flex items-center font-medium rounded-full">
            <Home className="w-5 h-5 mr-2" />
            Home
          </a>
        </LiquidButton>
        <LiquidButton asChild size="lg" variant="primary">
          <a href="/vote" className="inline-flex items-center font-medium rounded-full">
            <Trophy className="w-5 h-5 mr-2" />
            Vote
          </a>
        </LiquidButton>
        <LiquidButton asChild size="lg">
          <a href="/leaderboard" className="inline-flex items-center font-medium rounded-full">
            <TrendingUp className="w-5 h-5 mr-2" />
            Leaderboard
          </a>
        </LiquidButton>
      </div>

      <div className="max-w-6xl mx-auto pt-16">
        <div className="text-center mb-8">
          <p className="text-gray-300">
            Vote between these two DVHS alumni profiles
          </p>
        </div>

        <div className="relative flex justify-center items-center">
          {/* Profiles Container */}
          <div className="grid grid-cols-2 gap-4 sm:gap-8 w-full max-w-5xl">
            {/* Profile 1 */}
            <div className="relative">
              <div className={`${!voted ? 'pointer-events-none' : ''} transition-all duration-200`}>
                <AlumniProfileCard
                  profile={votingPair.profile1}
                  showElo={voted}
                  showLinkedIn={voted}
                  blurred={!voted}
                  eloChange={eloChanges?.profile1.change}
                  hoverLinkedIn={voted}
                />
              </div>
              {!voted && (
                <button
                  onClick={() => handleVote(votingPair.profile1.id)}
                  className="absolute inset-0 w-full h-full z-50 bg-transparent hover:bg-white/10 cursor-pointer rounded-lg transition-colors duration-200"
                />
              )}
            </div>

            {/* Profile 2 */}
            <div className="relative">
              <div className={`${!voted ? 'pointer-events-none' : ''} transition-all duration-200`}>
                <AlumniProfileCard
                  profile={votingPair.profile2}
                  showElo={voted}
                  showLinkedIn={voted}
                  blurred={!voted}
                  eloChange={eloChanges?.profile2.change}
                  hoverLinkedIn={voted}
                />
              </div>
              {!voted && (
                <button
                  onClick={() => handleVote(votingPair.profile2.id)}
                  className="absolute inset-0 w-full h-full z-50 bg-transparent hover:bg-white/10 cursor-pointer rounded-lg transition-colors duration-200"
                />
              )}
            </div>
          </div>

          {/* Center Action Button */}
          <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 z-20">
            {!voted ? (
              <LiquidButton
                onClick={() => handleVote()}
                className="w-20 h-20 sm:w-24 sm:h-24 rounded-full text-sm sm:text-base"
              >
                Equal
              </LiquidButton>
            ) : (
              <LiquidButton
                onClick={loadNewPair}
                variant="primary"
                className="w-20 h-20 sm:w-24 sm:h-24 rounded-full"
              >
                <ArrowRight className="w-6 h-6 sm:w-8 sm:h-8" />
              </LiquidButton>
            )}
          </div>
        </div>
      </div>
    </div>
  )
}

