        {query && (
          <button
            type="button"
            onClick={handleClear}
            className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600 z-20"
          >
            <X className="w-5 h-5" />
          </button>
        )}
      </div>
    </form>
  )
}

