app/globals.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
      'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
      sans-serif;
  }

  body {
    @apply bg-black text-white;
    min-height: 100vh;
    -webkit-overflow-scrolling: touch;
  }

  * {
    -webkit-tap-highlight-color: transparent;
  }
}

@layer components {
  .linkedin-button {
    @apply bg-linkedin-blue text-white px-6 py-2 rounded-full font-semibold hover:bg-linkedin-dark transition-colors duration-200;
  }

  .linkedin-button-outline {
    @apply border-2 border-linkedin-blue text-linkedin-blue px-6 py-2 rounded-full font-semibold hover:bg-linkedin-light transition-colors duration-200;
  }

  .profile-card {
    @apply p-6;
  }

  .search-input {
    @apply w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-linkedin-blue focus:border-transparent;
  }

  .blur-content {
    @apply blur-md select-none pointer-events-none;
  }

  .elo-change-positive {
    @apply text-green-600 font-semibold;
  }

  .elo-change-negative {
    @apply text-red-600 font-semibold;
  }

  /* Custom Scrollbar Styles */
  .scrollbar-custom::-webkit-scrollbar {
    width: 12px;
  }

  .scrollbar-custom::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 10px;
  }

  .scrollbar-custom::-webkit-scrollbar-thumb {
    background: linear-gradient(180deg, #3B82F6 0%, #2563EB 100%);
    border-radius: 10px;
    border: 2px solid rgba(255, 255, 255, 0.05);
  }

  .scrollbar-custom::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(180deg, #60A5FA 0%, #3B82F6 100%);
  }

  .scrollbar-custom::-webkit-scrollbar-thumb:active {
    background: linear-gradient(180deg, #93C5FD 0%, #60A5FA 100%);
  }
}

